import React from 'react';
import PropTypes from 'prop-types';
// import Button from '@material-ui/core/Button';
import { IconButton, Tooltip } from '@material-ui/core';
import AccountCircle from '@material-ui/icons/AccountCircle'
// import ClickAwayListener from '@material-ui/core/ClickAwayListener';
import Grow from '@material-ui/core/Grow';
import Paper from '@material-ui/core/Paper';
import Popper from '@material-ui/core/Popper';
import MenuItem from '@material-ui/core/MenuItem';
import MenuList from '@material-ui/core/MenuList';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    root: {
        display: 'flex',
    },
});

class SignOut extends React.Component {
    state = {
        open: false,
    };

    handleToggle = () => {
        this.setState(state => ({ open: !state.open }));
    };

    render() {
        const { classes } = this.props;
        const { open } = this.state;

        return (
            <div className={classes.root}>
                <div>
                    <IconButton buttonRef={node => {
                        this.anchorEl = node;
                    }}
                        aria-owns={open ? 'menu-list-grow' : undefined}
                        aria-haspopup="true"
                        onClick={this.handleToggle}>
                        <Tooltip title="Account" style={{ height: "26px", width: "26px" }}>
                            {/* <img src={require('/home/bridgeit/Shweta/fundooNotes/frontend/src/assets/images/account.png')} alt="refresh icon" /> */}
                            <AccountCircle />
                        </Tooltip>
                    </IconButton>
                    <Popper open={open} anchorEl={this.anchorEl} transition disablePortal>
                        {({ TransitionProps, placement }) => (
                            <Grow
                                {...TransitionProps}
                                id="menu-list-grow"
                                style={{ transformOrigin: placement === 'bottom' ? 'center top' : 'center bottom' }}
                            >
                                <Paper style={{ width: "300px", marginTop: "10px" }}>
                                    <div style={{ height:"50px",width:"100px"}}>
                                        {/* <MenuItem onClick={this.handleClose}>Profile</MenuItem> */}
                                        <AccountCircle />
                                    </div>
                                    <div>
                                        <MenuList style={{ display: "flex" }}>
                                            <MenuItem onClick={this.handleAddAccount}>Add account</MenuItem>
                                            <MenuItem onClick={this.handleLogout}>Logout</MenuItem>
                                        </MenuList>
                                    </div>
                                </Paper>
                            </Grow>
                        )}
                    </Popper>
                </div>
            </div>
        );
    }
}

SignOut.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SignOut);
